def chooseWardenMon
  blacklist = [:ETERNATUS,:REGIROCK,:REGICE,:REGISTEEL,:MANAPHY,:PHIONE,:DARKRAI,:CRESSELIA,:TAPUKOKO,:TAPULELE,:TAPUFINI,:TAPUBULU,:HOOPA]
  mon = selectWardenPokemon
  if mon != nil
    new_mon = pbTranslateWardenSpecies(mon,true)
    can_translate = (new_mon==nil) ? false : (TECH_WARD_PAIR.include?(new_mon) ? true : false)
    if can_translate && !blacklist.include?(mon)
      generateWardenMon(new_mon)
    else
      pbMessage("Unfortunately I can't make an egg for you.")
    end
  end
end

def selectWardenPokemon
  chosen = 0
  pbFadeOutIn {
    scene = PokemonParty_Scene.new
    screen = PokemonPartyScreen.new(scene,$Trainer.party)
    screen.pbStartScene(_INTL("Choose a Pokémon."),false)
    chosen = screen.pbChoosePokemon
    screen.pbEndScene
  }
  if chosen>=0
    pokemon=$Trainer.party[chosen].species
    return pokemon
  else
    return nil
  end
end

def pbTranslateWardenSpecies(species,dir = false)
  if dir
    cannonLength = TECH_WARD_PAIR.length
    option = []
    for i in 0...cannonLength
      cannon_dexnum = i if TECH_WARD_PAIR[i].include?(species)
      option.push(cannon_dexnum) if cannon_dexnum!=nil
    end
    option.uniq!
    new_dexnum = (option.length>1) ? option.sample : cannon_dexnum
    if new_dexnum != nil
     orion_dexnum = TECH_WARD_PAIR[new_dexnum][1]
     return orion_dexnum
    end
  else
	#orion_dexnum = $cache.pkmn[species].dexnum
    #new_dexnum = orion_dexnum-WARDEN_MIN
    #cannon_dexnum = TECH_WARD_PAIR[new_dexnum][0]
    ward_mon = (IS_WARDEN_MON.include?(species.species)) ? species.species : nil
    return ward_mon
  end
end

def generateWardenMon(ward_mon)
  new_mon = babySpecies(ward_mon)
  pbMessage("Understood, Take good care of them!")
  pbAddToParty(new_mon,1)
end
